const PCOLORS=["#f77189","#bb9832","#50b131","#36ada4","#3ba3ec","#e866f4"];class Bar{constructor(t,e,l,n,a,o,s){this.id=t;this.name=e;this.x_label=l;this.y_label=n;this.labels=a;this.values=o;this.log_toggle=s}}class MultiBar{constructor(t,e,l,n,a,o,s){this.id=t;this.names=e;this.x_label=l;this.y_label=n;this.labels=a;this.values=o;this.log_toggle=s}}class Hexbin{constructor(t,e,l,n,a){this.id=t;this.min=e;this.max=l;this.radius=n;this.bins=a}}class Heatmap{constructor(t,e,l,n,a){this.id=t;this.name=e;this.x_labels=l;this.y_labels=n;this.values=a}}function buildPlotDownload(e,l,n){document.getElementById("btn-download-plot-"+l).onclick=function(){var t=document.createElement("a");t.href=e.toBase64Image();t.download=n+"_"+l+".png";t.click()}}function buildTableDownload(l,t,n){document.getElementById("btn-download-table-"+t).onclick=function(){let t=new Blob([l],{type:"text/plain"});var e=document.createElement("a");e.href=URL.createObjectURL(t);e.download=n+"_table.tsv";e.click()}}function buildLogToggle(e,t){document.getElementById("btn-logscale-plot-"+t).addEventListener("change",function(t){if(t.currentTarget.checked){e.options.scales.y.type="logarithmic"}else{e.options.scales.y.type="linear"}e.update()})}function getColor(t,e){const l=(t-e)/(1-e);const n=1-l;const a=255-n*(255-179);const o=255-n*255;const s=255-n*255;const c="rgb("+a+","+o+","+s+")";return c}function buildColorSlider(t,e){document.getElementById("btn-colorscale-"+e).addEventListener("change",function(l){t.data.datasets[0].backgroundColor=t=>{const e=t.dataset.data[t.dataIndex].v;return getColor(e,l.currentTarget.value)};t.options.plugins.tooltip.callbacks={title(){return""},label(t){const e=t.dataset.data[t.dataIndex];return[e.x_label+" - ",e.y_label+":",e.v]}};t.update()})}
